import encryptdecrypt.Main;
import org.hyperskill.hstest.v5.stage.BaseStageTest;
import org.hyperskill.hstest.v5.testcase.PredefinedIOTestCase;

import java.util.List;

public class EncryptDecryptTest extends BaseStageTest {
    public EncryptDecryptTest() throws Exception {
        super(Main.class);
    }

    @Override
    public List<PredefinedIOTestCase> generate() {
        return List.of(
            new PredefinedIOTestCase(
                "enc\n" +
                    "Welcome to hyperskill!\n" +
                    "5",
                "\\jqhtrj%yt%m~ujwxpnqq&"),
            new PredefinedIOTestCase(
                "enc\n" +
                    "Hello\n" +
                    "0",
                "Hello"),
            new PredefinedIOTestCase(
                "enc\n" +
                    "012345678\n" +
                    "1",
                "123456789"),
            new PredefinedIOTestCase(
                "dec\n" +
                    "\\jqhtrj%yt%m~ujwxpnqq&\n" +
                    "5",
                "Welcome to hyperskill!"),
            new PredefinedIOTestCase(
                "dec\n" +
                    "Hello\n" +
                    "0",
                "Hello"),
            new PredefinedIOTestCase(
                "dec\n" +
                    "222233334444\n" +
                    "1",
                "111122223333")
        );
    }
}

package encryptdecrypt;

public class Main {
    public static void main(String[] args) {
        System.out.println("Hello World!");
    }
}

import encryptdecrypt.Main;
import org.hyperskill.hstest.v5.stage.BaseStageTest;
import org.hyperskill.hstest.v5.testcase.CheckResult;
import org.hyperskill.hstest.v5.testcase.TestCase;

import java.util.List;

public class EncryptDecryptTest extends BaseStageTest<String> {
    public EncryptDecryptTest() throws Exception {
        super(Main.class);
    }

    @Override
    public List<TestCase<String>> generate() {
        return List.of(
            new TestCase<String>()
                .addArguments(new String[]{
                    "-mode", "enc",
                    "-key", "5",
                    "-data", "Welcome to hyperskill!"
                })
                .setAttach("\\jqhtrj%yt%m~ujwxpnqq&"),

            new TestCase<String>()
                .addArguments(new String[]{
                    "-key", "0",
                    "-mode", "enc",
                    "-data", "Hello"
                })
                .setAttach("Hello"),

            new TestCase<String>()
                .addArguments(new String[]{
                    "-key", "1",
                    "-data", "012345678",
                    "-mode", "enc"
                })
                .setAttach("123456789"),

            new TestCase<String>()
                .addArguments(new String[]{
                    "-mode", "dec",
                    "-data", "\\jqhtrj%yt%m~ujwxpnqq&",
                    "-key", "5"
                })
                .setAttach("Welcome to hyperskill!"),

            new TestCase<String>()
                .addArguments(new String[]{
                    "-mode", "dec",
                    "-key", "0",
                    "-data", "Hi"
                })
                .setAttach("Hi"),

            new TestCase<String>()
                .addArguments(new String[]{
                    "-mode", "dec",
                    "-key", "1",
                    "-data", "222233334444"
                })
                .setAttach("111122223333")
        );
    }

    @Override
    public CheckResult check(String reply, String clue) {
        return new CheckResult(reply.trim().equals(clue.trim()));
    }
}

package encryptdecrypt;

public class Main {
    public static void main(String[] args) {
        System.out.println("Hello World!");
    }
}

import encryptdecrypt.Main;
import org.hyperskill.hstest.v5.common.Utils;
import org.hyperskill.hstest.v5.stage.BaseStageTest;
import org.hyperskill.hstest.v5.testcase.CheckResult;
import org.hyperskill.hstest.v5.testcase.TestCase;

import java.util.List;

public class EncryptDecryptTest extends BaseStageTest<String> {
    public EncryptDecryptTest() throws Exception {
        super(Main.class);
    }

    @Override
    public List<TestCase<String>> generate() {
        return List.of(
            new TestCase<String>()
                .addArguments(new String[]{
                    "-mode", "enc",
                    "-key", "5",
                    "-in", "in.txt",
                    "-out", "output.txt"
                })
                .setAttach("\\jqhtrj%yt%m~ujwxpnqq&")
                .addFile("in.txt", "Welcome to hyperskill!"),

            new TestCase<String>()
                .addArguments(new String[]{
                    "-key", "0",
                    "-mode", "enc",
                    "-in", "input.txt",
                    "-out", "output.txt"
                })
                .setAttach("Hello")
                .addFile("input.txt", "Hello"),

            new TestCase<String>()
                .addArguments(new String[]{
                    "-key", "1",
                    "-in", "i.txt",
                    "-out", "output.txt",
                    "-mode", "enc"
                })
                .setAttach("123456789")
                .addFile("i.txt", "012345678"),

            new TestCase<String>()
                .addArguments(new String[]{
                    "-mode", "dec",
                    "-in", "input.txt",
                    "-out", "output.txt",
                    "-key", "5"
                })
                .setAttach("Welcome to hyperskill!")
                .addFile("input.txt", "\\jqhtrj%yt%m~ujwxpnqq&"),

            new TestCase<String>()
                .addArguments(new String[]{
                    "-mode", "dec",
                    "-key", "0",
                    "-in", "input.txt",
                    "-out", "output.txt"
                })
                .setAttach("Hi")
                .addFile("input.txt", "Hi"),

            new TestCase<String>()
                .addArguments(new String[]{
                    "-mode", "dec",
                    "-key", "1",
                    "-in", "input.txt",
                    "-out", "output.txt"
                })
                .setAttach("111122223333")
                .addFile("input.txt", "222233334444")
        );
    }

    @Override
    public CheckResult check(String reply, String clue) {
        String output = Utils.readFile("output.txt");
        return new CheckResult(output.trim().equals(clue.trim()));
    }
}

